async function scrapeEvents(url, timezone = 'America/New_York') {
  const browser = await chromium.launch({ headless: true })
  const page = await browser.newPage()

  try {
    await page.goto(url, { waitUntil: 'domcontentloaded' })
    await page.waitForTimeout(3000)

    const html = await page.content()
    const $ = cheerio.load(html)

    var events = []

    // Check for JSON-LD structured data first
    $('script[type="application/ld+json"]').each((index, element) => {
      try {
        const jsonData = JSON.parse($(element).html())
        if (jsonData['@type'] === 'Event' || (Array.isArray(jsonData) && jsonData.some(item => item['@type'] === 'Event'))) {
          const eventData = Array.isArray(jsonData) ? jsonData.filter(item => item['@type'] === 'Event') : [jsonData]
          eventData.forEach(event => {
            if (event.startDate) {
              events.push({
                title: event.name || '',
                startsAt: new Date(event.startDate),
                sourceUrl: event.url || url,
                description: event.description || '',
                imageUrl: event.image?.url || event.image || '',
                ticketUrl: event.offers?.url || '',
                endsAt: event.endDate ? new Date(event.endDate) : null
              })
            }
          })
        }
      } catch (e) {
        // Continue if JSON parsing fails
      }
    })

    // If no structured data found, parse HTML
    if (events.length === 0) {
      for (let i = 0; i < $('.event_card').length; i++) {
        const $card = $('.event_card').eq(i)

        const title = $card.find('.event_card_title h5').text().trim()
        const presenter = $card.find('.event_card_presents').text().trim()
        const dateTimeText = $card.find('.event_card_date_times').text().trim()
        const imageUrl = $card.find('.event_card_image img').attr('src')
        const detailsLink = $card.find('.event_card_details_button a').attr('href')

        if (!title || !dateTimeText) continue

        // Visit detail page to get more information
        let description = ''
        let descriptionHtml = ''
        let ticketUrl = detailsLink
        let coverCharge = ''
        let ageRestriction = ''

        if (detailsLink && detailsLink.includes('aomtheatre.com')) {
          try {
            const detailPage = await browser.newPage()
            await detailPage.goto(detailsLink, { waitUntil: 'domcontentloaded' })
            await detailPage.waitForTimeout(2000)

            const detailHtml = await detailPage.content()
            const $detail = cheerio.load(detailHtml)

            // Check for JSON-LD on detail page
            $detail('script[type="application/ld+json"]').each((index, element) => {
              try {
                const jsonData = JSON.parse($detail(element).html())
                if (jsonData['@type'] === 'Event') {
                  description = jsonData.description || ''
                  if (jsonData.offers) {
                    if (jsonData.offers.price) {
                      coverCharge = jsonData.offers.price === '0' ? 'Free' : '$' + jsonData.offers.price
                    }
                    ticketUrl = jsonData.offers.url || ticketUrl
                  }
                }
              } catch (e) {
                // Continue if JSON parsing fails
              }
            })

            // Extract description from HTML if not found in JSON-LD
            if (!description) {
              const descriptionSelectors = [
                '.event-description',
                '.entry-content p',
                '.et_pb_text_inner p',
                '.event-details',
                '.description'
              ]

              for (const selector of descriptionSelectors) {
                const desc = $detail(selector).first().text().trim()
                if (desc && desc.length > 50) {
                  description = desc
                  descriptionHtml = $detail(selector).first().html()
                  break
                }
              }
            }

            // Look for ticket information - handle relative URLs
            const ticketSelectors = [
              'a[href*="ticket"]',
              'a[href*="buy"]',
              'a[href*="purchase"]',
              '.ticket-link',
              '.buy-tickets',
              'a[href*="eventbrite"]',
              'a[href*="brownpapertickets"]'
            ]

            for (const selector of ticketSelectors) {
              let ticketLink = $detail(selector).attr('href')
              if (ticketLink) {
                // Handle relative URLs
                if (ticketLink.startsWith('/')) {
                  ticketLink = 'https://aomtheatre.com' + ticketLink
                } else if (!ticketLink.startsWith('http')) {
                  ticketLink = 'https://aomtheatre.com/' + ticketLink
                }

                // Don't use the event detail page itself as ticket URL
                if (!ticketLink.includes('/aom_event/')) {
                  ticketUrl = ticketLink
                  break
                }
              }
            }

            // Look for price information
            const priceText = $detail('body').text()
            const priceMatches = priceText.match(/\$(\d+(?:\.\d{2})?)/g)
            if (priceMatches) {
              coverCharge = priceMatches[0]
            } else if (priceText.toLowerCase().includes('free')) {
              coverCharge = 'Free'
            }

            // Look for age restriction
            if (priceText.toLowerCase().includes('21+') || priceText.toLowerCase().includes('21 and over')) {
              ageRestriction = 'TWENTY_ONE_PLUS'
            } else if (priceText.toLowerCase().includes('18+') || priceText.toLowerCase().includes('18 and over')) {
              ageRestriction = 'EIGHTEEN_PLUS'
            } else if (priceText.toLowerCase().includes('all ages')) {
              ageRestriction = 'ALL_AGES'
            }

            await detailPage.close()
          } catch (e) {
            // Continue if detail page fails to load
          }
        }

        // Parse different date formats
        let eventDates = []

        // Handle multi-date events like "12/12 4pm & 7:30, 12/13 1pm & 4:30pm, 12/14 1pm & 4:30pm"
        if (dateTimeText.includes('&') && dateTimeText.includes(',')) {
          const parts = dateTimeText.split(',')
          parts.forEach(part => {
            part = part.trim()
            if (part.includes('/')) {
              const dateMatch = part.match(/(\d{1,2}\/\d{1,2})\s+(.+)/)
              if (dateMatch) {
                const datePart = dateMatch[1]
                const timePart = dateMatch[2]

                const times = timePart.split('&').map(t => t.trim())
                times.forEach(time => {
                  const timeMatch = time.match(/(\d{1,2}):?(\d{0,2})\s*(am|pm)?/i)
                  if (timeMatch) {
                    let hour = parseInt(timeMatch[1])
                    const minute = timeMatch[2] ? parseInt(timeMatch[2]) : 0
                    const ampm = timeMatch[3] || (hour < 8 ? 'pm' : 'am')

                    if (ampm.toLowerCase() === 'pm' && hour !== 12) hour += 12
                    if (ampm.toLowerCase() === 'am' && hour === 12) hour = 0

                    const [month, day] = datePart.split('/').map(n => parseInt(n))
                    const year = new Date().getFullYear() + (month < 6 ? 1 : 0)

                    const eventDate = fromZonedTime(new Date(year, month - 1, day, hour, minute), timezone)
                    eventDates.push(eventDate)
                  }
                })
              }
            }
          })
        }
        // Handle single date formats like "Saturday, December 20th, 2025 at 6:00pm"
        else if (dateTimeText.includes('at')) {
          const match = dateTimeText.match(/(\w+),?\s+(\w+)\s+(\d{1,2})\w*,?\s+(\d{4})\s+at\s+(\d{1,2}):(\d{2})\s*(am|pm)/i)
          if (match) {
            const monthName = match[2]
            const day = parseInt(match[3])
            const year = parseInt(match[4])
            let hour = parseInt(match[5])
            const minute = parseInt(match[6])
            const ampm = match[7]

            if (ampm.toLowerCase() === 'pm' && hour !== 12) hour += 12
            if (ampm.toLowerCase() === 'am' && hour === 12) hour = 0

            const monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 
                               'July', 'August', 'September', 'October', 'November', 'December']
            const month = monthNames.findIndex(m => m.toLowerCase().startsWith(monthName.toLowerCase()))

            if (month !== -1) {
              const eventDate = fromZonedTime(new Date(year, month, day, hour, minute), timezone)
              eventDates.push(eventDate)
            }
          }
        }
        // Handle date ranges like "March 19th, 2026 - March 21st, 2026"
        else if (dateTimeText.includes(' - ') && !dateTimeText.includes('at')) {
          const match = dateTimeText.match(/(\w+)\s+(\d{1,2})\w*,?\s+(\d{4})\s+-\s+(\w+)\s+(\d{1,2})\w*,?\s+(\d{4})/)
          if (match) {
            const startMonth = match[1]
            const startDay = parseInt(match[2])
            const startYear = parseInt(match[3])

            const monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 
                               'July', 'August', 'September', 'October', 'November', 'December']
            const month = monthNames.findIndex(m => m.toLowerCase().startsWith(startMonth.toLowerCase()))

            if (month !== -1) {
              const eventDate = fromZonedTime(new Date(startYear, month, startDay, 19, 30), timezone)
              eventDates.push(eventDate)
            }
          }
        }
        // Handle formats like "May 12-13, 2026 at 7pm"
        else if (dateTimeText.includes('-') && dateTimeText.includes('at')) {
          const match = dateTimeText.match(/(\w+)\s+(\d{1,2})-(\d{1,2}),?\s+(\d{4})\s+at\s+(\d{1,2})\s*(am|pm)/i)
          if (match) {
            const monthName = match[1]
            const startDay = parseInt(match[2])
            const endDay = parseInt(match[3])
            const year = parseInt(match[4])
            let hour = parseInt(match[5])
            const ampm = match[6]

            if (ampm.toLowerCase() === 'pm' && hour !== 12) hour += 12
            if (ampm.toLowerCase() === 'am' && hour === 12) hour = 0

            const monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 
                               'July', 'August', 'September', 'October', 'November', 'December']
            const month = monthNames.findIndex(m => m.toLowerCase().startsWith(monthName.toLowerCase()))

            if (month !== -1) {
              for (let day = startDay; day <= endDay; day++) {
                const eventDate = fromZonedTime(new Date(year, month, day, hour, 0), timezone)
                eventDates.push(eventDate)
              }
            }
          }
        }

        // Create events for each date
        eventDates.forEach(eventDate => {
          const event = {
            title: title,
            startsAt: eventDate,
            sourceUrl: detailsLink || url
          }

          if (imageUrl) event.imageUrl = imageUrl
          if (description) event.description = description
          if (descriptionHtml) event.descriptionHtml = descriptionHtml
          if (ticketUrl && ticketUrl !== detailsLink) event.ticketUrl = ticketUrl
          if (coverCharge) event.coverCharge = coverCharge
          if (ageRestriction) event.ageRestriction = ageRestriction

          if (presenter) {
            event.artists = [{ name: presenter, isHeadliner: true }]
          }

          events.push(event)
        })
      }
    }

    await browser.close()
    return events.filter(event => event.startsAt > new Date()).sort((a, b) => a.startsAt - b.startsAt)
  } catch (error) {
    await browser.close()
    throw error
  }
}
